import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { map } from 'rxjs';
import { Product } from './model/products';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'angular-http';
  allProducts: Product[] = [];
  isFetching: boolean = false;
  constructor(private http: HttpClient) {}
  ngOnInit() {
    this.fetchProducts();
  }
  onProductsFetch() {
    this.fetchProducts();
  }
  onProductCreate(products: { pName: string; desc: string; price: string }) {
    console.log(products);
    const headers = new HttpHeaders({ myHeader: 'procademy' });

    this.http
      .post<{ name: string }>(
        'https://angularbymahfuz-default-rtdb.firebaseio.com/products.json',
        products,
        { headers: headers }
      )
      .subscribe((res) => {
        console.log(res);
      });
  }
  private fetchProducts() {
    this.isFetching = true;
    this.http
      .get<{ [key: string]: Product }>(
        'https://angularbymahfuz-default-rtdb.firebaseio.com/products.json'
      )
      .pipe(
        map((res) => {
          const products = [];
          for (const key in res) {
            if (res.hasOwnProperty(key)) {
              products.push({ ...res[key], id: key });
            }
          }
          return products;
        })
      )
      .subscribe((products) => {
        console.log(products);
        this.allProducts = products;
        this.isFetching = false;
      });
  }
  onDeleteProduct(id: string) {
    this.http
      .delete(
        'https://angularbymahfuz-default-rtdb.firebaseio.com/products/' +
          id +
          '.json'
      )
      .subscribe();
  }
  onDeleteAllProducts() {
    this.http
      .delete(
        'https://angularbymahfuz-default-rtdb.firebaseio.com/products.json'
      )
      .subscribe();
  }
}
<div class="main-area">
  <div class="content-area">
    <div class="header">
      <h1>Manage Products</h1>
      <hr />
    </div>
    <div class="container">
      <!--Add product form-->
      <div class="form-area">
        <h3>Create Product</h3>
        <form
          #productsForm="ngForm"
          (ngSubmit)="onProductCreate(productsForm.value)"
        >
          <label>Procuct Name</label>
          <input type="text" name="pName" ngModel />

          <label>Procuct Description</label>
          <input type="text" name="desc" ngModel />

          <label>Procuct Price</label>
          <input type="text" name="price" ngModel />

          <input type="submit" value="Add Product" />
        </form>
      </div>
      <div class="product-display-area">
        <h3>All Products</h3>
        <table id="products">
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Description</th>
            <th>Price</th>
            <th></th>
            <th></th>
          </tr>
          <tr *ngIf="allProducts.length < 1 && !isFetching">
            <td colspan="5">No Products Available!</td>
          </tr>

          <tr *ngIf="isFetching">
            <td colspan="5">Loading....</td>
          </tr>
          <tr *ngFor="let prod of allProducts; index as i">
            <td>{{ i + 1 }}</td>
            <td>{{ prod.pName }}</td>
            <td>{{ prod.desc }}</td>
            <td>{{ prod.price }}</td>
            <td>
              <button class="btn-delete" (click)="onDeleteProduct(prod.id)">
                Delete
              </button>
            </td>
            <td>
              <button class="btn-edit">Edit</button>
            </td>
          </tr>
        </table>
        <input
          type="submit"
          value="Fetch Procuct"
          (click)="onProductsFetch()"
        />
        <hr />
        <div class="action-btn-container">
          <button class="btn-fetch" (click)="onProductsFetch()">
            Fetch Product
          </button>
          <button class="btn-clear" (click)="onDeleteAllProducts()">
            Clear Product
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
